# I have create this file - Pratikfrom django.http import HttpResponsefrom django.shortcuts import renderdef index(request):    return render(request, 'index.html')def analyze(request):    # Get The Text    djtext = request.POST.get('text', 'defult')    # Check Checkbox value    removepunc = request.POST.get('removepunc', 'off')    fullcaps = request.POST.get('fullcaps', 'off')    newlineremover = request.POST.get('newlineremover', 'off')    extraremover = request.POST.get('extraremover', 'off')    # Check which Checkbox on    if removepunc == "on":        punctuations = '''!()-[]{};:'"\,<>./?@#$%^&*_~'''        analyzed = ""        for char in djtext:            if char not in punctuations:                analyzed = analyzed + char        params = {'purpose': 'Remove Punctuation', 'analyzed_text': analyzed}        djtext = analyzed        # return render(request, 'analyze.html', params)    if fullcaps == 'on':        analyzed = ''        for char in djtext:            analyzed = analyzed + char.upper()        params = {'purpose': 'Changed to Uppercase', 'analyzed_text': analyzed}        djtext = analyzed        # return render(request, 'analyze.html', params)    if newlineremover == 'on':        analyzed = ''        for char in djtext:            if char != "\n" and char != "\r":                analyzed = analyzed + char        params = {'purpose': 'Removed New Lines', 'analyzed_text': analyzed}        djtext = analyzed        # return render(request, 'analyze.html', params)    if extraremover == 'on':        analyzed = ""        for index, char in enumerate(djtext):            if djtext[index] == " " and djtext[index + 1] == " ":                pass            else:                analyzed = analyzed + char        params = {'purpose': 'Extra Space Removed', 'analyzed_text': analyzed}    if removepunc != "on" and fullcaps != "on" and newlineremover != "on" and extraremover != "on":        return HttpResponse("Please select any operation and try again")    return render(request, 'analyze.html', params)